import React from 'react';
import './App.css';
import TodoInput from './components/TodoInput';
import { useSelector } from 'react-redux';
import Todotask from './components/Todotask';

function App() {
  // Adjust the selector to correctly access the todo list
  const list = useSelector((state) => state.todos.value);
  console.log("Total value is: ", list);

  return (
    <div className='wrapper'>
      <h1 className='title'>TODO APP</h1>
      <TodoInput />

      {list.length === 0 ? (
        <h2 className='no-task'>No tasks available</h2>
      ) : (
        list.map((listItem, i) => (
          <Todotask key={i} task={listItem} />
        ))
      )}
    </div>
  );
}

export default App;
